import React, { Component } from 'react';
import './App.css';
import Box from './Components/Board.js'

class App extends Component {
  constructor(props){
    super(props)
    // Variables
    this.state = {
        player: "-",
        playable: Array(9).fill(true)
    }
  }

  // Functions
  createBoard = () => {
    let boxs = []

    for (let i = 0; i < 9; i++){
      boxs.push(<Box 
                    id = {i} 
                    value = {this.state.player}/>)
    }
    return boxs
  }

  changeValue(){
    this.setState({
      player: this.state.player === "X" ? "O" : "X",
    })
  }

  render() {
    return (
      <div
        className = "container">
        <div
          className = "board"
          onClick = {() => this.changeValue()}>
          {this.createBoard()}            
        </div>
      </div>
    );
  }
}

export default App;
