import React, { Component } from 'react';
import './App.css';
import Board from './Components/Board'
import Square from './Components/Square'

class App extends Component {
constructor(){
    super()
    this.state = {
        boardField: Array(9).fill(Array(9).fill("-"))
    }
}

changeValue(i){
    console.log(i)
    var auxSquare = this.state.boardField
    auxSquare[0][i] = "X"
    this.setState({
        boardField: auxSquare
    });
    console.log(auxSquare[0][i])
}

createBoard = (square) => {
    var board = []
    for(var i = 0; i < 9; i++){
        board.push(<div className = "SquareSmall" onClick = {() => this.changeValue(i)}> {square[i]} </div>)
    }
    return board
}

createGame = (board) => {
    var game = []
    for(var i = 0; i < 9; i++){
        game.push(
            <div className = "SquareBig">
                <div className = "Board">
                    {this.createBoard(board[i])}
                </div>
            </div>
        )
    }
    return game
}

render() {
    return (
    <div className="App">
        <h1>Ultimate Tic Tac Toe</h1>
        <div className="MainGame">
            {this.createGame(this.state.boardField)}
        </div>
    </div>
    );
}
}

export default App;
