import React, { Component } from 'react';
import './App.css';
import Box from './Components/Box.js'

class App extends Component {
  constructor(props){
    super(props)
    // Variables
    this.state = {
        player: "-"
    }
  }

  // Functions
  createBoard = () => {
    let boxs = []

    for (let i = 0; i < 9; i++){
      boxs.push(<Box 
                    id = {i} 
                    value = {this.state.player}/>)
    }
    return boxs
  }

    changeValue(){
      this.setState({
        player: this.state.player === "X" ? "O" : "X"
      })
    }



  render() {
    return (
      <div 
        onClick = {() => this.changeValue()}
      >
      {this.createBoard()}            
      </div>
    );
  }
}

export default App;
