import React, { Component } from 'react';
import './../App.css';
import Square from './Square'

class Board extends Component {
    constructor(props){
        super(props)
        this.state = {
            squareField: this.props.value
        }
    }

    changeValue(i){
        var auxSquare = this.state.squareField
        auxSquare[i] = "X"
        this.setState = {
            squareField: auxSquare
        }
    }

    createBoard = () => {
        var board = []
        for(var i = 0; i < 9; i++){
            board.push(<div onClick = {() => changeValue(i)}><Square value = {this.state.squareField[i]} size = {"Small"} key = {i + "" + this.props.value}/></div>)
        }
        return board
    }

    render() {
        return (
            <div className="Board">
                {this.createBoard()}
            </div>
        );
    }
}

export default Board;
