import React, { Component } from 'react';
import './../App.css';

class Square extends Component {
    constructor(props){
        super(props)
        this.state = {
            value: this.props.value,
            able: "Able"
        }
    }

    changeValue(){
        if(this.props.flag === "1"){
            this.setState({
                able: "Disable",
                value: this.state.value === "X" ? "O" : "X"
            });
        }
    }

    render() {
    return (
        <div className= {"Square" + this.props.size} id = {this.props.size + this.state.able} key = {this.props.key} onClick = {() => this.changeValue()}>
            {this.state.value}
        </div>
    );
  }
}

export default Square;
