import React, { Component } from 'react';
import './App.css';

class App extends Component {
    constructor(){
        super()
        this.state = {
            boardField: Array(81)
        }
    }

    changeValue(i){
        console.log(i)
        var auxSquare = Array(9).fill(Array(9).fill("X"))
        console.log(this.state.boardField[0][0])
        this.setState({
            boardField: auxSquare
        });
    }

    createBoard = (square) => {
        var board = []
        for(var i = 0; i < 9; i++){
            board.push(<div className = "SquareSmall" onClick = {() => this.changeValue(i)} id = "1"> </div>)
        }
        return board
    }

    createGame = (board) => {
        for(var i = 0; i < 81; i++){
            this.state.board[i] = "-"
        }
        var game = []
        for(var i = 0; i < 9; i++){
            game.push(
                <div className = "SquareBig">
                    <div className = "Board">
                        {this.createBoard(board[i])}
                    </div>
                </div>
            )
        }
        return game
    }

    render() {
        return (
        <div className="App">
            <h1>Ultimate Tic Tac Toe</h1>
            <div className="MainGame">
                {this.createGame(this.state.boardField)}
            </div>
        </div>
        );
    }
}

export default App;
