import React, { Component } from 'react';
import './../App.css';

class Board extends Component {
    constructor(props){
        super(props)
        this.state = {
            squareField: this.props.value
        }
    }

    changeValue(i){
        console.log("Teste 2")
        var newField = this.state.squareField
        newField[i] = "X"
        this.setState = {
            squareField: []
        }
    }

    createBoard = () => {
        this.state.squareField[0] = "x"
        var board = []
        for(var i = 0; i < 9; i++){
            board.push(<div className= {"SquareSmall"} key = {this.props.key} onClick = {() => this.changeValue(i)}>
                {this.state.squareField[i]}
            </div>)
        }
        return board
    }

    render() {
        return (
            <div className="Board">
                {this.createBoard()}
            </div>
        );
    }
}

export default Board;
