import React, { Component } from 'react';
import './../App.css';
import Square from './Square'

class Board extends Component {
    constructor(props){
        super(props)
        this.state = {
            squareField: this.props.value
        }
    }

    changeValue(e){
        console.log(e.target.key)
    }

    createBoard = () => {
        var board = []
        for(var i = 0; i < 9; i++){
            board.push()
        }
        return board
    }

    render() {
        return (
            <div className="Board">
                <div onClick = {(e) => this.changeValue(e)}><Square value = {"X"} size = {"Small"} key = {this.props.value}/></div>
            </div>
        );
    }
}

export default Board;
