import React, { Component } from 'react';
import './App.css';

class App extends Component {
    constructor(){
        super()
        this.state = {
            player: "X",
            boardField: [   Array(9).fill("-"),Array(9).fill("-"),Array(9).fill("-"),
                            Array(9).fill("-"),Array(9).fill("-"),Array(9).fill("-"),
                            Array(9).fill("-"),Array(9).fill("-"),Array(9).fill("-")]
        }
    }

    changeValue(e, j){
        var auxSquare = this.state.boardField
        auxSquare[j][e.target.id] = this.state.player
        console.log(auxSquare)
        this.setState({
            boardField: auxSquare,
            player: this.state.player === "X" ? "O" : "X"
        });
    }

    createBoard = (square,j) => {
        var board = []
        for(var i = 0; i < 9; i++){
            board.push(<div id = {i} className = "SquareSmall" onClick = {(e) => this.changeValue(e, j)}>{square[i]}</div>)
        }
        return board
    }

    createGame = (board) => {
        var game = []
        for(var i = 0; i < 9; i++){
            game.push(
                <div className = "SquareBig">
                    <div className = "Board">
                        {this.createBoard(board[i],i)}
                    </div>
                </div>
            )
        }
        return game
    }

    render() {
        return (
        <div className="App">
            <h1>Ultimate Tic Tac Toe</h1>
            <div className="MainGame">
                {this.createGame(this.state.boardField)}
            </div>
        </div>
        );
    }
}

export default App;
