import React, { Component } from 'react';
import './App.css';

class App extends Component {
    constructor(){
        super()
        this.state = {
            boardField: [Array(9).fill("-"),Array(9).fill("-"),Array(9).fill("-"),Array(9).fill("-"),Array(9).fill("-"),Array(9).fill("-")]
        }

        var teste = this.state.boardField
        teste[0][0] = "C"
        console.log(teste)

        this.setState({
            boardField: teste
        })
    }

