    }

    createBoard = (square) => {
        console.log(square)
        var board = []
        for(var i = 0; i < 9; i++){
            board.push(<div className = "SquareSmall" onClick = {() => this.changeValue(square)}>{square[i]}</div>)
        }
        return board
    }

    createGame = () => {
        var game = []
        for(var i = 0; i < 9; i++){
            game.push(
                <div className = "SquareBig">
                    <div className = "Board">
                        {this.createBoard(this.state.boardField[i])}
                    </div>
                </div>
            )
        }
        return game
    }

    render() {
        return (
        <div className="App">
            <h1>Ultimate Tic Tac Toe</h1>
            <div className="MainGame">
                {this.createGame()}
            </div>
        </div>
        );
    }
}

export default App;
